import HttpAccess from "../HttpAccess";

const getAll = (value) => {
    let output;
    output = HttpAccess.get(`/${value}`).then(err => {
        if (err.response){
            output = "Something went wrong with the API";
        }
        else if(err.request){
            output = "We are unable to access the API right now"
        }
        else{
            output = "Something went wrong"
        }
    })
    console.log(output)
    return output;
}

export default {getAll};
