import HttpAccess from "../HttpAccess";

const getAll = (value) => {
    let output;
    output = HttpAccess.get(`/${value}`).catch(err => {
        if (err.response){
            output = "Something went wrong with the API".then(console.log(output));
        }
        else if(err.request){
            output = "We are unable to access the API right now".then(console.log(output));
        }
        else{
            output = "Something went wrong".then(console.log(output));
        }
    })
    return output.PromiseResult.message;
}

export default {getAll};
